import styles from "./App.module.scss";
import HeaderComponent from "./components/header/Header";
import SideBar from "./components/sideBar/SideBar";
import MediaComponent from "./components/media/Media";
import LibraryImport from "./components/libraryImport/LibraryImport";
import MassEditor from "./components/massEditor/massEditor";
import Queue from "./components/queue/Queue";
import History from "./components/history/History";
import Settings from "./components/settings/Settings";
import Profiles from "./components/profiles/Profiles";
import MediaManagement from "./components/mediaManagement/MediaManagement";
import General from "./components/general/General";
import UI from "./components/ui/UI";
import Status from "./components/status/Status";
import Tasks from "./components/tasks/Tasks";
import Backup from "./components/backup/Backup";
import Updates from "./components/updates/Updates";
import Events from "./components/events/Events";
import LogFiles from "./components/logFiles/LogFiles";
import { useCallback, useEffect, useMemo, useState } from "react";

import {
	BrowserRouter as Router,
	Route,
	Routes,
	Link,
	useNavigate,
	Outlet,
	useParams,
} from "react-router-dom";

function App() {
	const [selectedItem, setSelectedItem] = useState<number>(-1);
	const [selectedOption, setSelectedOption] = useState<number>(0);

	useEffect(() => {
		// Extract the path from the current URL
		const currentPath = location.pathname;

		// Map your sidebar items to their corresponding paths
		const sidebarItems = {
			"/home": "Home",
			"/about": "About",
			"/contact": "Contact",
		};

		// Find the matching sidebar item for the current path
		const matchedItem = Object.keys(sidebarItems).find((path) =>
			currentPath.startsWith(path)
		);

		// Set the active item in the state
		setSelectedItem(0);
		setSelectedOption(0);
	}, [location.pathname]);

	return (
		<div className={styles.app}>
			<HeaderComponent />
			<div className={styles.content}>
				<SideBar
					selectedOption={memoizedSelectedOptionState}
					setSelectedOption={memoizedSetSelectedOption}
					selectedItem={memoizedSelectedItemState}
					setSelectedItem={memoizedSetSelectedItem}
				/>
				<Router>
					<Routes>
						<Route path="/" element={<MediaComponent />} />
						<Route path="/series/:seriesName" element={<Series />} />
						<Route path="/library-import" element={<LibraryImport />} />
						<Route path="/mass-editor" element={<MassEditor />} />

						<Route path="/activity" element={<Activity />} />
						<Route path="/activity/queue" element={<Queue />} />
						<Route path="/activity/history" element={<History />} />

						<Route path="/settings" element={<Settings />} />
						<Route
							path="/settings/media-management"
							element={<MediaManagement />}
						/>
						<Route path="/settings/profiles" element={<Profiles />} />
						<Route path="/settings/general" element={<MediaComponent />} />
						<Route path="/settings/status" element={<Status />} />

						<Route path="/system" element={<System />} />
						<Route path="/system/status" element={<Status />} />

						{
							//<Route path="*" element={<NotFound />} />
						}
					</Routes>
				</Router>
			</div>
		</div>
	);

	function System() {
		const navigate = useNavigate();

		useEffect(() => {
			navigate("/system/status");
		}, [navigate]);
		return null;
	}

	function Activity() {
		const navigate = useNavigate();

		useEffect(() => {
			navigate("/activity/queue");
		}, [navigate]);
		return null;
	}
	function Series() {
		const { seriesName } = useParams();

		return <h2>Series Name: {seriesName}</h2>;
	}

	function NotFound() {
		const navigate = useNavigate();

		useEffect(() => {
			navigate("/");
		}, [navigate]);
		return null;
	}
}

export default App;
