import { useState } from "react";
import useSettings from "../../hooks/useSettings";
import styles from "./MediaManagement.module.scss";
const MediaManagement = () => {
	const settings = useSettings();

	const settingsArray: any[] | (() => any[]) = [];
	for (let i in settings) {
		settingsArray.push(settings[i]);
	}

	const [newSettings, setNewSettings] = useState(settingsArray);

	return (
		<div className={styles.mediaManagement}>
			{newSettings.map((setting: any) => (
				<div>
					<label>
						{setting?.name}
						<input
							type="input"
							value={setting?.value}
							onChange={(e) => {
								setting.value = e.target.value;
							}}
						/>
					</label>
				</div>
			))}
		</div>
	);
};
export default MediaManagement;
