import { useEffect, useState } from "react";
import ToolBar from "../ToolBar/ToolBar";
import styles from "./Series.module.scss";
import ToolBarItem from "../ToolBarItem/ToolBarItem";
import VisibilityIcon from "@mui/icons-material/Visibility";
import FilterAltIcon from "@mui/icons-material/FilterAlt";
import AppsIcon from "@mui/icons-material/Apps";
import RssFeedIcon from "@mui/icons-material/RssFeed";
import SyncIcon from "@mui/icons-material/Sync";
import SwitchLeftIcon from "@mui/icons-material/SwitchLeft";
import Season from "../season/Season";

const Series = ({ seriesName }: any) => {
	const leftToolBarItems: any = [
		<ToolBarItem text="Update" icon={<SyncIcon fontSize="large" />} />,
		<ToolBarItem text="RSS Sync" icon={<RssFeedIcon fontSize="medium" />} />,
	];

	const middleToolBarItems: any = [
		<ToolBarItem text="Options" icon={<AppsIcon fontSize="large" />} />,
	];
	const rightToolBarItems: any = [
		<ToolBarItem text="View" icon={<VisibilityIcon fontSize="medium" />} />,
		<ToolBarItem text="Sort" icon={<SwitchLeftIcon fontSize="medium" />} />,
		<ToolBarItem text="Filter" icon={<FilterAltIcon fontSize="medium" />} />,
	];

	const [series, setSeries] = useState([]);
	const [meta, setMeta] = useState([]);
	useEffect(() => {
		fetch("http://localhost:8000/data/series")
			.then((response) => response.json())
			.then((data) => setSeries(data))
			.catch((error) => console.error(error));
	}, []);

	useEffect(() => {
		fetch("http://localhost:8000/data/series/meta?series_name=" + seriesName)
			.then((response) => response.json())
			.then((data) => setMeta(data))
			.catch((error) => console.error(error));
	}, []);

	seriesName = seriesName.replace(/-/g, " ");

	const seriesData: any = series.filter(
		(obj: any) => obj.series === seriesName
	)[0];

	const seriesMeta: any = meta;

	const status = seriesMeta?.status;
	const networks = seriesMeta.networks ? seriesMeta.networks : [];
	const network = networks.length >= 1 ? networks[0].name : undefined;

	const genres = seriesMeta.genres ? seriesMeta.genres : [];
	const mainGenre = genres.length >= 1 ? genres[0].name : undefined;

	const firstAirDate = seriesMeta?.first_air_date?.split("-")[0].trim();
	const lastAirDate = seriesMeta?.last_air_date?.split("-")[0].trim();
	const overview = seriesMeta?.overview;
	const runYears =
		status === "Ended" ? firstAirDate + "-" + lastAirDate : firstAirDate + "-";

	const seasons: any = [];
	for (var i = 1; i++; i < seriesMeta?.seasons?.length) {
		const seasonFileInfo = seriesData?.seasons.filter(
			(season: any) => season.season == i
		);
		const seasonMetaInfo = seriesMeta?.seasons?.filter(
			(season: any) => season.season_number == i
		);

		seasons.push();
	}

	return (
		<div className={styles.series}>
			<ToolBar
				leftToolBarItems={leftToolBarItems}
				middleToolBarItems={middleToolBarItems}
				rightToolBarItems={rightToolBarItems}
			/>
			<div className={styles.header}>
				<img
					className={styles.backdrop}
					src={"/metadata/series/" + seriesName + "/backdrop.jpg"}
					alt="backdrop"
				/>
				<div className={styles.filter}></div>
				<div className={styles.content}>
					<img
						className={styles.poster}
						src={"/metadata/series/" + seriesName + "/poster.jpg"}
						alt={"poster"}
					/>
					<div className={styles.details}>
						<div className={styles.titleRow}>{seriesData?.series}</div>
						<div className={styles.seriesDetails}>
							<span className={styles.runtime}>
								{seriesMeta?.episode_run_time} Minutes
							</span>
							<span className={styles.genre}>{mainGenre}</span>
							<span className={styles.runYears}>{runYears}</span>
						</div>
						<div className={styles.tags}>
							<div className={styles.tag}>/tv/American Dad!</div>

							<div className={styles.tag}>245.9GB</div>
							<div className={styles.tag}>ProfileHD</div>
							<div className={styles.tag}>Monitored</div>
							<div className={styles.tag}>{status}</div>
							<div className={styles.tag}>{network}</div>
						</div>
						<div className={styles.overview}>{overview}</div>
					</div>
				</div>
			</div>
			<div className={styles.seasonsContainer}>
				{seasons.map((season: any, key: any) => (
					<Season season />
				))}
			</div>
		</div>
	);
};
export default Series;
