import SideBarItem from "../sideBarItem/SideBarItem";
import styles from "./SideBar.module.scss";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import AccessTimeIcon from "@mui/icons-material/AccessTime";
import SettingsIcon from "@mui/icons-material/Settings";
import LaptopMacIcon from "@mui/icons-material/LaptopMac";
import { useState } from "react";
//
const MenuComponent = () => {
	const seriesOptions = {
		text: "Series",
		svg: <PlayArrowIcon />,
		children: [
			{
				text: "Add New",
			},
			{
				text: "Library Import",
			},
			{
				text: "Mass Editor",
			},
			{
				text: "Season Pass",
			},
		],
	};

	const activityOptions = {
		text: "Activity",
		svg: <AccessTimeIcon />,
		children: [
			{
				text: "Queue",
			},
			{
				text: "History",
			},
		],
	};

	const settingsOptions = {
		text: "Settings",
		svg: <SettingsIcon />,
		children: [
			{
				text: "Queue",
			},
			{
				text: "History",
			},
		],
	};

	const systemOptions = {
		text: "System",
		svg: <LaptopMacIcon />,
		children: [
			{
				text: "Queue",
			},
			{
				text: "History",
			},
		],
	};

	const [selectedOption, setSelectedOption] = useState<number>(0);

	return (
		<div className={styles.sideBar}>
			<SideBarItem
				options={seriesOptions}
				onClick={() => setSelectedOption(0)}
			/>
			<SideBarItem options={activityOptions} onClick={setSelectedOption(1)} />
			<SideBarItem options={settingsOptions} onClick={setSelectedOption(2)} />
			<SideBarItem options={systemOptions} onClick={setSelectedOption(3)} />
		</div>
	);
};
export default MenuComponent;
