import { useEffect, useState } from "react";
import styles from "./EditModal.module.scss";
const EditModal = ({ isOpen, onClose, header, content, footer }: any) => {
	const [codecs, setCodecs] = useState([]);

	useEffect(() => {
		fetch("http://localhost:8000/codecs")
			.then((response) => response.json())
			.then((data) => setCodecs(data))
			.catch((error) => console.error(error));
	}, []);
	if (!isOpen) return null;

	const onSaveProfile = async () => {
		await fetch("");
		onClose();
		console.log("saved");
	};

	const onCancel = () => {
		onClose();
	};

	return (
		<div className={styles.modal}>
			<div className={styles.header}>
				{header}
				<button className="close-button" onClick={onClose}>
					Close
				</button>
			</div>
			<div className={styles.content}>{content}</div>
			<div className={styles.footer}>
				<div className={styles.left}>
					<div className={styles.delete}>Delete</div>
				</div>
				<div className={styles.right}>
					<div className={styles.cancel} onClick={() => onCancel()}>
						Cancel
					</div>
					<div className={styles.save} onClick={() => onSaveProfile()}>
						Save
					</div>
				</div>
			</div>
		</div>
	);
};
export default EditModal;
