import { useEffect, useState } from "react";
import styles from "./Modal.module.scss";
const Modal = ({ isOpen, onClose, children, profile }: any) => {
	const [codecs, setCodecs] = useState([]);
	useEffect(() => {
		fetch("http://localhost:8000/codecs")
			.then((response) => response.json())
			.then((data) => setCodecs(data))
			.catch((error) => console.error(error));
	}, []);
	if (!isOpen) return null;

	return (
		<div className={styles.modalOverlay}>
			<div className={styles.modal}>
				<div className={styles.header}>
					Edit Codec Profile
					<button className="close-button" onClick={onClose}>
						Close
					</button>
				</div>
				<div className={styles.left}>
					<h2>Name: {profile?.name}</h2>
				</div>
				<div className={styles.right}>
					<h3>Codecs</h3>
					<div className={styles.codecs}>
						{codecs.map((codec: string) => (
							<div>
								{codec} {profile?.codecs?.includes(codec) ? <>X</> : <></>}
							</div>
						))}
					</div>
				</div>
				{children}
			</div>
		</div>
	);
};
export default Modal;
