const Modal = ({ isOpen, onClose, children }: any) => {
	if (!isOpen) return null;

	return (
		<div className="modalOverlay">
			<div className="modal">
				<button className="close-button" onClick={onClose}>
					Close
				</button>
				{children}
			</div>
		</div>
	);
};
export default Modal;
