import styles from "./Poster.module.scss";
const PosterComponent = ({ id, data }: any) => {
	const status = "Monitored";
	const profile = "AV1";
	const progress = "80%";

	const posterPath = "/metadata/series/" + data.series + "/poster.jpg";

	const onErrorHandler = (event: { target: { src: any } }) => {
		// When the image fails to load, set the source to the fallback image.
		event.target.src = "/missing.png";
	};

	return (
		<div className={styles.cardArea}>
			<div className={styles.card}>
				<div className={styles.cardContent}>
					{
						//<div className={styles.ended}></div>
					}
					<img
						className={styles.img}
						src={posterPath}
						onError={onErrorHandler}
						alt={data.series}
					></img>
					<div className={styles.footer}>
						<div className={styles.progressBar}>
							<div
								className={styles.progress}
								style={{ width: progress }}
							></div>
						</div>
						<div className={styles.status}>{status}</div>
						<div className={styles.profile}>{profile}</div>
					</div>
				</div>
			</div>
		</div>
	);
};

export default PosterComponent;
