import React, { useEffect, useState } from "react";
import useSettings from "../../hooks/useSettings";
import styles from "./MediaManagement.module.scss";

const MediaManagement = () => {
	const settings = useSettings();
	const [formSettings, setFormSettings] = useState(settings);

	useEffect(() => {
		setFormSettings(settings);
	}, [settings]);
	console.log(settings);
	const handleInputChange: any = (name: any, value: any) => {
		const updatedFormSettings: any = formSettings.map((setting: any) =>
			setting.name === name ? { ...setting, value } : setting
		);
		setFormSettings(updatedFormSettings);
	};

	const handleSubmit = () => {
		console.log("Updated Settings:", formSettings);
		// Implement your logic to submit the updated settings
	};

	if (!formSettings.length) {
		// Render a loading state or null while settings are being fetched
		return null;
	}
	console.log(formSettings);

	return (
		<div className={styles.mediaManagement}>
			{formSettings.map((setting: any) => (
				<div key={setting.name}>
					<label>
						{setting.name}
						<input
							type="text"
							value={setting.value}
							onChange={(e) => handleInputChange(setting.name, e.target.value)}
						/>
					</label>
				</div>
			))}
			<button onClick={handleSubmit}>Submit</button>
		</div>
	);
};

export default MediaManagement;
