import { useState } from "react";
import styles from "./SeriesModals.module.scss";
import useProfiles from "../../hooks/useProfiles";

const SeriesModals = ({ type, content, setContent }: any) => {
	const EditContent = () => {
		const { profiles } = useProfiles();
		return (
			<div className={styles.content}>
				<div>
					<label>
						Monitored
						<input
							type="checkbox"
							checked={content.monitored}
							onChange={(e) =>
								setContent({ ...content, monitored: e.target.checked })
							}
						/>
					</label>
				</div>
				<div>
					<label>
						Select an Option
						<select
							value={content.selectedOption}
							onChange={(e) =>
								setContent({ ...content, selectedProfile: e.target.value })
							}
						>
							{profiles.map((profile: any) => (
								<option value={profile.name}>{profile.name}</option>
							))}
						</select>
					</label>
				</div>
			</div>
		);
	};

	return type === "edit" ? <EditContent /> : <></>;
};
export default SeriesModals;
