import styles from "./Profiles.module.scss";
import Profile from "../profile/Profile";
import { useEffect, useState } from "react";
import Modal from "../modal/Modal";

const Profiles = () => {
	const [profiles, setProfiles] = useState([]);
	const [codecs, setCodecs] = useState([]);

	const [profile, setProfile] = useState<any>({});

	const [isModalOpen, setIsModalOpen] = useState(false);

	const openModal = (profile: any) => {
		setProfile(profile);
		setIsModalOpen(true);
	};

	const closeModal = () => {
		setIsModalOpen(false);
	};

	useEffect(() => {
		fetch("http://localhost:8000/profiles")
			.then((response) => response.json())
			.then((data) => setProfiles(data))
			.catch((error) => console.error(error));
	}, []);

	return (
		<div className={styles.profiles}>
			<div className={styles.content}>
				<div className={styles.modal}>
					<Modal isOpen={isModalOpen} onClose={closeModal} profile={profile} />
				</div>
				<div className={styles.codecProfiles}>
					<div className={styles.header}>Codec Profiles</div>
					<div className={styles.profileContainer}>
						{profiles.map((profile: any) => (
							<div onClick={() => openModal(profile)}>
								<Profile name={profile?.name} codecs={profile?.codecs} />
							</div>
						))}
					</div>
				</div>
			</div>
		</div>
	);
};
export default Profiles;
