import { useState } from "react";
import styles from "./Season.module.scss";
const Season = ({ seasonData }: any) => {
	const episodes = [];
	for (let i in seasonData["episodes"]) {
		episodes.push(seasonData["episodes"][i]);
	}

	const [isOpen, setIsOpen] = useState(false);

	const onSeasonClick = () => {
		setIsOpen(!isOpen);
	};
	console.log(isOpen);

	return (
		<div className={styles.season}>
			<div className={styles.seasonHeader}>
				<div className={styles.left}>
					<div className={styles.seasonNumber}>{seasonData?.name}</div>
					<div className={styles.episodeRatio}>
						{} / {}
					</div>
					<div className={styles.profileRatio}>
						{} / {}
					</div>
					<div className={styles.size}>{11.2} GB</div>
					<div className={styles.open} onClick={onSeasonClick}>
						Open
					</div>
				</div>
			</div>
			<div
				className={styles.seasonInfo}
				style={isOpen ? {} : { display: "none" }}
			>
				<table>
					<thead>
						<tr>
							<th>#</th>
							<th>Title</th>
							<th>Air Date</th>
							<th>Status</th>
							<th>Size</th>
						</tr>
					</thead>
					<tbody>
						{episodes.map((episode) => (
							<tr>
								<td>{episode?.episode_number}</td>
								<td>{episode?.episode_name}</td>
								<td>{episode?.air_date}</td>
								<td>{episode?.video_codec}</td>
								<td>{episode?.size}</td>
							</tr>
						))}
					</tbody>
				</table>
				<div className={styles.seasonInfoFooter} onClick={onSeasonClick}>
					Close
				</div>
			</div>
		</div>
	);
};
export default Season;
