import { useEffect, useState } from "react";
import ToolBar from "../ToolBar/ToolBar";
import PosterComponent from "../poster/Poster";
import styles from "./Series.module.scss";

const Series = ({ seriesName }: any) => {
	const [series, setSeries] = useState([]);

	useEffect(() => {
		fetch("http://localhost:8000/data/series")
			.then((response) => response.json())
			.then((data) => setSeries(data))
			.catch((error) => console.error(error));
	}, []);

	console.log(seriesName);
	seriesName = seriesName.replace(/-/g, " ");

	return (
		<div className={styles.series}>
			<ToolBar />
			<div className={styles.header}>
				<img
					className={styles.backdrop}
					src={"/metadata/series/" + seriesName + "/backdrop.jpg"}
					alt="backdrop"
				/>
				<div className={styles.content}>
					<PosterComponent
						data={series.find((series: any) => series.series === "seriesName")}
					/>
				</div>
			</div>
			{seriesName}
		</div>
	);
};
export default Series;
