import styles from "./Settings.module.scss";
const Settings = ({
	selectedOption,
	setSelectedOption,
	selectedItem,
	setSelectedItem,
}: any) => {
	const clickSetting = (setting: string) => {
		if (setting === "mediaManagement") {
			setSelected(0);
		} else if (setting === "profiles") {
			setSelectedItem(1);
		} else if (setting === "general") {
			setSelectedItem(2);
		}
	};
	return (
		<div className={styles.settings}>
			<div className={styles.setting}>
				<div
					className={styles.header}
					onClick={() => clickSetting("mediaManagement")}
				>
					Media Management
				</div>
				<div className={styles.body}>
					Naming, file management settings and root folders
				</div>
			</div>
			<div className={styles.setting}>
				<div className={styles.header} onClick={() => clickSetting("profiles")}>
					Profiles
				</div>
				<div className={styles.body}>
					Quality, Language, Delay and Release profiles
				</div>
			</div>
			<div className={styles.setting}>
				<div className={styles.header} onClick={() => clickSetting("general")}>
					General
				</div>
				<div className={styles.body}>
					Port, SSL, username/password, proxy, analytics and updates
				</div>
			</div>
		</div>
	);
};
export default Settings;
