import styles from "./SeriesModals.module.scss";
const SeriesModals = ({ type }: any) => {
	const editContent = (
		<div className={styles.content}>
			<div>
				<label>
					Monitored
					<input
						type="checkbox"
						checked={content.monitored}
						onChange={(e) =>
							setContent({ ...content, monitored: e.target.checked })
						}
					/>
				</label>
			</div>
			<div>
				<label>
					Select an Option
					<select
						value={content.selectedOption}
						onChange={(e) =>
							setContent({ ...content, selectedOption: e.target.value })
						}
					>
						<option value="">Select...</option>
						<option value="option1">Option 1</option>
						<option value="option2">Option 2</option>
						{/* Add more options as needed */}
					</select>
				</label>
			</div>
		</div>
	);

	return type === "edit" ? editContent : <></>;
};
export default SeriesModals;
