import React, { useState, useEffect } from "react";
import useSettings from "../../hooks/useSettings";
import styles from "./MediaManagement.module.scss";

const MediaManagement = () => {
	const initialSettings = useSettings();
	const [settings, setSettings] = useState([]);

	useEffect(() => {
		// Update the state with the initial settings when they become available
		const s: any = [];
		for (let i in initialSettings) {
			s.push(initialSettings[i]);
		}
		setSettings(s);
	}, [initialSettings]);

	const handleInputChange = (name: any, value: any) => {
		// Find the setting in the state and update its value
		const updatedSettings: any = settings.map((setting: any) =>
			setting.name === name ? { ...setting, value } : setting
		);

		// Update the state with the new settings
		setSettings(updatedSettings);
	};
	console.log(s);
	return (
		<div className={styles.mediaManagement}>
			{settings.map((setting: any) => (
				<div key={setting.name}>
					<label>
						{setting.name}
						<input
							type="input"
							value={setting.value}
							onChange={(e) => handleInputChange(setting.name, e.target.value)}
						/>
					</label>
				</div>
			))}
		</div>
	);
};

export default MediaManagement;
