import styles from "./SideBarItem.module.scss";
import Link from "react-router-dom";
const SideBarItem = ({
	options,
	selected,
	selectedOption,
	selectedItem,
	setSelectedItem,
}: any) => {
	const onItemClick = (option: number) => {
		setSelectedItem(option);
		changePage();
	};

	const changePage = () => {
		if (options.id === 0) {
			if (selectedItem === -1) {
				window.history.replaceState(null, "", "/");
			} else if (selectedItem === 0) {
				window.location.href = "/library-import";
			} else if (selectedItem === 1) {
				window.location.href = "/mass-editor";
			}
		} else if (options.id === 1) {
			if (selectedItem === -1) {
				setSelectedItem(0);
			} else if (selectedItem === 0) {
				window.location.href = "/activity/queue";
			} else if (selectedItem === 1) {
				window.location.href = "/activity/history";
			}
		} else if (options.id === 2) {
			if (selectedItem === -1) {
				window.location.href = "/settings/media-management";
			} else if (selectedItem === 0) {
				window.location.href = "/settings/media-management";
			} else if (selectedItem === 1) {
				window.location.href = "/settings/profiles";
			} else if (selectedItem === 2) {
				window.location.href = "/settings/general";
			}
		} else if (options.id === 3) {
			if (selectedItem === -1) {
				//setSelectedItem(0);
				console.log("here");
				window.location.href = "/system/status";
			} else if (selectedItem === 0) {
				window.location.href = "/system/status";
			}
		}
	};

	return (
		<div
			className={styles.sideBarItem}
			style={selected ? { borderLeft: "3px solid #2193b5" } : {}}
		>
			<div
				className={styles.mainItem}
				style={selected ? { color: "#35c5f4", backgroundColor: "#252833" } : {}}
				onClick={() => {
					setSelectedItem(-1);
				}}
			>
				<div className={styles.svg}>{options.svg}</div>
				{options.text}
			</div>
			{selected ? (
				options.children.map((child: any, index: any) => (
					<div
						className={styles.childItem}
						key={index}
						onClick={() => onItemClick(index)}
						style={selectedItem === index ? { color: "#35c5f4" } : {}}
					>
						{child.text}
					</div>
				))
			) : (
				<></>
			)}
		</div>
	);
};
export default SideBarItem;
