import { useEffect, useState } from "react";
import styles from "./Modal.module.scss";
import SeriesModals from "../seriesModals/SeriesModals";
const Modal = ({ isOpen, setIsOpen, header, type, series_data }: any) => {
	const onClose = () => {
		setIsOpen(false);
	};

	const onDelete = () => {};
	const onSave = async () => {
		series_data.profile = content.selectedProfile;
		for (const seasonNumber in series_data["seasons"]) {
			series_data["seasons"][seasonNumber].profile = content.selectedProfile;
			for (const episodeNumber in series_data["seasons"][seasonNumber][
				"episodes"
			]) {
				series_data["seasons"][seasonNumber]["episodes"][
					episodeNumber
				].profile = content.selectedProfile;
			}
		}
		const response = await fetch(
			`http://localhost:8000/series/${series_data.name}`,
			{
				method: "PUT",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify({ series_data: series_data }),
			}
		);
	};

	useEffect(() => {
		const handleKeyDown = (event: KeyboardEvent) => {
			if (event.key === "Escape") {
				setIsOpen(false);
			}
		};
		window.addEventListener("keydown", handleKeyDown);
		return () => {
			window.removeEventListener("keydown", handleKeyDown);
		};
	}, [setIsOpen]);

	const [content, setContent] = useState({
		monitored: false,
		selectedProfile: "",
	});

	if (!isOpen) return null;
	return (
		<div className={styles.modal}>
			<div className={styles.header}>
				<div className={styles.left}>{header}</div>
				<div className={styles.right}>
					<button className="close-button" onClick={onClose}>
						Close
					</button>
				</div>
			</div>
			<div className={styles.content}>
				<SeriesModals type={type} content={content} setContent={setContent} />
			</div>
			<div className={styles.footer}>
				<div className={styles.left}>
					<div className={styles.delete} onClick={onDelete}>
						Delete
					</div>
				</div>
				<div className={styles.right}>
					<div className={styles.cancel} onClick={onClose}>
						Cancel
					</div>
					<div className={styles.save} onClick={onSave}>
						Save
					</div>
				</div>
			</div>
		</div>
	);
};
export default Modal;
