import { useEffect, useState } from "react";
import ToolBar from "../ToolBar/ToolBar";
import styles from "./Series.module.scss";

const Series = ({ seriesName }: any) => {
	const [series, setSeries] = useState([]);

	useEffect(() => {
		fetch("http://localhost:8000/data/series")
			.then((response) => response.json())
			.then((data) => setSeries(data))
			.catch((error) => console.error(error));
	}, []);

	seriesName = seriesName.replace(/-/g, " ");

	const seriesData: any = series.filter(
		(obj: any) => obj.series === seriesName
	)[0];
	console.log(seriesData);
	return (
		<div className={styles.series}>
			<ToolBar />
			<div className={styles.header}>
				<img
					className={styles.backdrop}
					src={"/metadata/series/" + seriesName + "/backdrop.jpg"}
					alt="backdrop"
				/>
				<div className={styles.filter}></div>
				<div className={styles.content}>
					<img
						className={styles.poster}
						src={"/metadata/series/" + seriesName + "/poster.jpg"}
						alt={"poster"}
					/>
					<div className={styles.details}>
						<div className={styles.titleRow}>{seriesData?.series}</div>
						<div className={styles.seriesDetails}>
							<span className={styles.runtime}>22 Minutes</span>
							<span className={styles.genre}>Horror</span>
							<span className={styles.runYears}>1999-2016</span>
						</div>
						<div className={styles.overview}>
							CIA Agent Stan Smith is constantly fighting off terrorists who
							threaten the safety of the USA (sometimes causing more harm than
							good). His wife Francine is an average housewife with two kids:
							Hayley, an average rebellious teenager and Steve, a geek who
							enjoys Dungeons and Dragons, gaming and comic book conventions.
							Then there’s Roger, the home’s illegal (space) alien who the
							Smith’s are keeping protected. Last but not least, there’s Klaus,
							the Smiths’ wisecracking talking fish (swapped from his human body
							years ago by the CIA).
						</div>
					</div>
				</div>
			</div>
			<div className={styles.seasonsContainer}>
				{seriesData?.seasons.map((season: any, key: any) => (
					<div className={styles.season}>
						<div className={styles.seasonInfo}>
							<div className={styles.left}>
								<div className={styles.seasonNumber}>Season {key}</div>
							</div>
						</div>
					</div>
				))}
			</div>
		</div>
	);
};
export default Series;
