import React, { useEffect, useState } from "react";
import useSettings from "../../hooks/useSettings";
import styles from "./MediaManagement.module.scss";

const MediaManagement = () => {
	const initialSettings = useSettings();
	const [settings, setSettings] = useState(initialSettings);

	useEffect(() => {
		setSettings(initialSettings);
	}, [initialSettings]);

	console.log(settings);
	const handleInputChange = (name: any, value: any) => {
		const updatedSettings: any = settings.map((setting: any) =>
			setting.name === name ? { ...setting, value } : setting
		);
		setSettings(updatedSettings);
	};

	const handleSubmit = () => {
		console.log("Updated Settings:", settings);
		// Implement your logic to submit the updated settings
	};

	if (!settings.length) {
		// Render a loading state or null while settings are being fetched
		return null;
	}

	return (
		<div className={styles.mediaManagement}>
			{settings.map((setting: any) => (
				<div key={setting.name}>
					<label>
						{setting.name}
						<input
							type="text"
							value={setting.value}
							onChange={(e) => handleInputChange(setting.name, e.target.value)}
						/>
					</label>
				</div>
			))}
			<button onClick={handleSubmit}>Submit</button>
		</div>
	);
};

export default MediaManagement;
