import SideBarItem from "../sideBarItem/SideBarItem";
import styles from "./SideBar.module.scss";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import AccessTimeIcon from "@mui/icons-material/AccessTime";
import SettingsIcon from "@mui/icons-material/Settings";
import LaptopMacIcon from "@mui/icons-material/LaptopMac";
import { useState } from "react";
const MenuComponent = () => {
	const mediaOptions = {
		text: "Media",
		svg: <PlayArrowIcon />,
		children: [
			{
				text: "Library Import",
			},
			{
				text: "Mass Editor",
			},
		],
	};

	const activityOptions = {
		text: "Activity",
		svg: <AccessTimeIcon />,
		children: [
			{
				text: "Queue",
			},
			{
				text: "History",
			},
		],
	};

	const settingsOptions = {
		text: "Settings",
		svg: <SettingsIcon />,
		children: [
			{
				text: "Queue",
			},
			{
				text: "History",
			},
		],
	};

	const systemOptions = {
		text: "System",
		svg: <LaptopMacIcon />,
		children: [
			{
				text: "Status",
			},
			{
				text: "Tasks",
			},
			{
				text: "Backup",
			},
			{
				text: "Updates",
			},
			{
				text: "Tasksß",
			},
			{
				text: "Tasksß",
			},
		],
	};

	const [selectedOption, setSelectedOption] = useState<number>(0);

	const onItemClick = (option: number) => {
		setSelectedOption(option);
	};

	return (
		<div className={styles.sideBar}>
			<div onClick={() => onItemClick(0)}>
				<SideBarItem options={mediaOptions} selected={selectedOption === 0} />
			</div>
			<div onClick={() => onItemClick(1)}>
				<SideBarItem
					options={activityOptions}
					selected={selectedOption === 1}
				/>
			</div>
			<div onClick={() => onItemClick(2)}>
				<SideBarItem
					options={settingsOptions}
					selected={selectedOption === 2}
				/>
			</div>

			<div onClick={() => onItemClick(3)}>
				<SideBarItem options={systemOptions} selected={selectedOption === 3} />
			</div>
		</div>
	);
};
export default MenuComponent;
