import styles from "./Profiles.module.scss";
import Profile from "../profile/Profile";
import { useState } from "react";
import Modal from "../modal/Modal";
import useProfiles from "../../hooks/useProfiles";
import ToolBar from "../ToolBar/ToolBar";
import ToolBarItem from "../ToolBarItem/ToolBarItem";
import SyncIcon from "@mui/icons-material/Sync";

const Profiles = () => {
	const profiles = useProfiles();
	const [modalType, setModalType] = useState("");
	const [isModalOpen, setIsModalOpen] = useState(false);
	const [selectedProfile, setSelectedProfile] = useState<any>({});
	const handleProfileClick = (profile: any) => {
		setSelectedProfile(profile);
		setContent({
			name: profile?.name,
			codec: profile?.codec,
			codecs: profile?.codecs,
			speed: profile?.speed,
		});
		setModalType("profile");
		setIsModalOpen(true);
	};

	const onModalSave = async (profile: any) => {
		const response = await fetch(
			`http://localhost:8000/profiles/${profile.name}`,
			{
				method: "PUT",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify({ profile: profile }),
			}
		);
	};

	const [content, setContent] = useState({
		name: "",
		codec: "",
		codecs: [],
		speed: "",
	});

	const leftToolBarItems: any = [
		<ToolBarItem text="Advanced" icon={<SyncIcon fontSize="large" />} />,
	];

	return (
		<div className={styles.profiles}>
			<ToolBar leftToolBarItems={leftToolBarItems} />
			{isModalOpen && modalType === "profile" && (
				<div className={styles.modalBackdrop}>
					<div className={styles.modalContent}>
						<Modal
							header={"Edit - " + selectedProfile?.name}
							type={"profile"}
							isOpen={isModalOpen}
							setIsOpen={setIsModalOpen}
							onSave={onModalSave}
							data={selectedProfile}
							content={content}
							setContent={setContent}
						/>
					</div>
				</div>
			)}
			<div className={styles.content}>
				<div className={styles.codecProfiles}>
					<div className={styles.header}>Codec Profiles</div>
					<div className={styles.profileContainer}>
						{profiles?.map((profile: any) => (
							<div onClick={() => handleProfileClick(profile)}>
								<Profile name={profile?.name} codecs={profile?.codecs} />
							</div>
						))}
					</div>
				</div>
			</div>
		</div>
	);
};
export default Profiles;
